        public Task<List<PostItem>> GetPostItemsAsync(PostSubCategories postSubCategories)
        {
            List<PostItem> postItemList = new List<PostItem>();
            switch (postSubCategories)
            {
                case PostSubCategories.Testimonails:
                    postItemList.Add(new PostItem { Id = new Guid("1795BAD7-0B1E-4DFB-A3EF-38EA048F2CB1"), PostId = 1, Name = "“อบอุ่น ปลอดภัย เข้ามาอยู่แล้วรู้สึกว่าพี่อยู่คนเดียวได้เลย”", Description = "<span style=\"color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\">ที่นี่อบอุ่นปลอดภัย เข้ามาอยู่แล้วรู้สึกว่าพี่อยู่คนเดียวได้เลย และที่ดูแล้วชอบคือมันเป็นทาวน์โฮมแบบที่ไม่เหมือนทาวน์โฮม</span>", PostCategories = PostCategories.DLFamily, PostSubCategories = PostSubCategories.Testimonails, TitleImage = "images/post/family/1/DL-Family_01.jpg", Content = "<p><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\">ที่นี่อบอุ่นปลอดภัย เข้ามาอยู่แล้วรู้สึกว่าพี่อยู่คนเดียวได้เลย และที่ดูแล้วชอบคือมันเป็นทาวน์โฮมแบบที่ไม่เหมือนทาวน์โฮม<br />ที่นี่คล้ายบ้านเดี่ยว เพราะทาวน์โฮมสมัยใหม่เนี้ยมันเหมือนกล่องเหมือนบล็อก ความรู้สึกมันไม่เหมือนบ้าน<br />เรื่องของวัสดุอุปกรณ์ที่ใช้ก็โอเค</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">ครอบครัว คุณวิศาล&nbsp;</b><b style=\" font-weight: bolder\">ทฤษฎิคุ</b><b style=\" font-weight: bolder\">ณ (คุณหนุ่ย)</b><br />จากโครงการ Dream Deluxe</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">คุณหนุ่ยช่วยเล่าเรื่องตอนหาบ้านแล้วมาเจอที่นี่หน่อยค่ะ</b><br />ตอนนั้นต้องมาหาบ้านกัน<br />ในระยะเวลาเพียง 2 อาทิตย์ ตอนหาแรกๆเนี่ย<br />แม่อยากให้อยู่ใกล้ๆกันแถวแจ้งวัฒนะ<br />แต่แถวนั้นค่อนข้างราคาสูง<br />เราเลยลองไปดูแถวซอยวัดพระเงินทางกาญจนา<br />เสร็จแล้วปรากฏว่าน้องสาวอยู่ที่เอดรีม<br />(หนึ่งในโครงการของดรีมแลนด์)<br />เค้าเลยมาช่วยหาให้ด้วย<br />เค้าเลยมาบอกว่ามีโครงการดรีมดีลักซ์<br />เข้าทางดรีมวิลล์ก็เลยลองเข้ามาดู</p><p style=\" margin: 0px 0px 15px\">เข้ามาช่วงแรกเนี่ยโครงการกำลังสร้าง<br />สิ่งแรกที่ประทับใจ พอเข้ามาแล้วชอบคือ<br />มันเป็นทาวน์โฮมแบบที่ไม่เหมือนทาวน์โฮม<br />ที่นี่คล้ายบ้านเดี่ยว เพราะทาวน์โฮมสมัยใหม่<br />มันเหมือนกล่องเหมือนบล็อก<br />ความรู้สึกมันไม่เหมือนบ้าน ดูแล้วชอบ<br />จากนั้นเราก็เลยมาดูบ้าน<br />ดูเรื่องของวัสดุอุปกรณ์ที่ใช้ก็โอเค<br />และตกลงใจที่จะเลือกบ้านหลังมุมที่อยู่ท้ายซอยที่มีเนื้อที่<br />เพื่อให้ความรู้สึกที่เหมือนบ้านเดี่ยว<br />และเงียบกว่า ก็เลยเลือกที่นี่<br />ถูกใจที่นี่ที่เดียวเลย แล้ววันนั้นก็พาแม่ยายมาดู<br />แม่ยายก็ชอบบอกว่าจองเลยลูก (หัวเราะ)<br />จองวันแม่ด้วย แล้วผมก็นัดญาติๆพี่น้อง<br />มาฉลองวันแม่กัน 12 สิงหา ปี59 พอดี</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">ที่คุณหนุ่ยบอกว่ารู้สึกโดนเลย พอจะขยายความได้ไหมคะ ว่าเป็นส่วนไหน</b><br />อย่างที่บอกครับ พอเข้ามาในบ้านแล้ว<br />ลักษณะมันไม่เหมือนทาวน์โฮมสมัยใหม่<br />ในส่วนของตัวบ้าน ผมไม่ชอบลักษณะเป็นกล่องๆ<br />ที่นี่หลังคาเป็นทรงปั้นหยา<br />ให้ความรู้สึกเหมือนบ้านเดี่ยว<br />แล้วผมเข้ามาตอนที่โครงการกำลังสร้างพอดี<br />ผมเห็นว่าผนังบ้านเองเนี่ยมันขึ้นสุดถึงหลังคา<br />ซึ่งถ้าเป็นบางโครงการอื่นเนี่ย<br />เค้าไม่ได้ทำสุดถึงหลังคา แล้วก็อย่างที่บอกว่า<br />พอดูแล้วรู้สึกว่าวัสดุหรืออะไรเนี่ย<br />ก็ให้เหมือนกับบ้านไม่เหมือนทาวน์โฮม ดูแข็งแรง</p><p style=\" margin: 0px 0px 15px\">นอกจากของหรือวัสดุที่ใช้แล้ว<br />น้องสาวผมซื้อโครงการ<br />แล้วตอนน้ำท่วมโครงการก็ดูแล<br />ที่ได้ใจไปเต็มๆ คือที่นี่เค้าไม่ทิ้งลูกบ้าน<br />ถึงคุณจะไปตั้งนิติบุคคลแล้วเนี่ย<br />ทางโครงการก็ยังช่วยดูแลตลอด<br />เหมือนเป็นสายใยที่ไม่ทิ้งกัน<br />แล้วประทับใจยิ่งกว่านั้นว่าหลังจากจองบ้านไปแล้ว<br />เข้ามาดูบ้านบ่อยๆก็จะเจอเจ้าของโครงการ<br />ผมว่ามันคือการลงรายละเอียด<br />และความใส่ใจ ที่ทำให้ผมประทับใจ</p><p style=\" margin: 0px 0px 15px\">อีกส่วนนึงที่เลือก ผมก็ไปดูโครงการอื่นด้วย<br />เข้าไปในซอยวัดบางไกรใน ซึ่งเป็นบ้านเดี่ยว<br />เกือบจะซื้อด้วยแล้วนะ แต่ผมรู้สึกว่า<br />เนื้อที่ต่างกันนิดหน่อย แต่เขาไม่ได้ทำอะไรเพิ่มให้<br />ตรงหลังคาตรงครัวที่เป็นบ้านแฝดเนี่ย<br />จากอีกบ้านนึงมันสามารถเดินข้ามหลังคามาบ้านเราได้<br />รู้สึกไม่ปลอดภัย และที่อื่นเนี่ยด้านหลังจะไม่ได้ลงเข็ม<br />เทปูนและปูกระเบื้องให้เหมือนที่นี่<br />อีกอย่างรู้สึกว่าดรีมแลนด์<br />เก็บเกี่ยวประสบการณ์มาปรับใช้<br />เช่นปลั๊กไฟยกสูงขึ้น<br />และมีการทำรางน้ำให้ด้วย</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">มีเรื่องไหนอีกไหมคะที่ประทับใจ</b><br />เวลามีอะไร ที่โครงการก็ดูแลให้อย่างดี<br />พี่เคยมีครั้งนึง น้ำไม่ไหล<br />เพราะพี่ไปต่างประเทศ<br />แล้วน้องเขยเค้าปิดวาวล์หน้าบ้านให้<br />พอพี่กลับมาแล้ว น้ำไม่ไหลพี่ก็โทรติดต่อเซลล์<br />เค้าก็ดีมากช่วยโทรตามประปา<br />อะไรให้หมดเลยที่แท้ไม่ได้เปิดวาวล์ (หัวเราะ)</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">แล้วน้องสาวเค้าไปซื้อ เอดรีมได้ยังไงคะ</b><br />เค้าซื้อก่อน เพราะจะแต่งงานแล้วย้ายบ้าน<br />เค้าก็ชวนพี่เหมือนกัน แต่ตอนนั้นประมาณปี 55<br />เรายังไม่คิดจะซื้อบ้าน อีกส่วนน้องเขยอยู่ระนอง<br />พอเค้าหรือคุณพ่อคุณแม่เข้ามามันก็ใกล้ที่นี่<br />และทุกคนก็รู้สึกเหมือนกันว่า<br />ที่นี่เข้ามาแล้วมันรู้สึกเหมือนบ้าน</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">สรุปความประทับใจเกี่ยวกับโครงการ หรือบริษัทสั้นๆ</b><br />อบอุ่นปลอดภัย เข้ามาอยู่แล้วรู้สึกปลอดภัย พี่อยู่คนเดียวพี่อยู่ได้</p></div></div></p>", CreatedAt = new DateTime(2017, 11, 11), ShowGallery = false });
                    postItemList.Add(new PostItem { Id = new Guid("71AFE15E-A124-4667-8F92-C8375D874282"), PostId = 2, Name = "“มาดูเห็นปุ๊บแล้วเราก็ซื้อเลย”", Description = "<span style=\"color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\">ที่ตัดสินใจซื้อที่นี่ตอนนั้นไม่รู้จักแบรนด์ ดรีมแลนด์เลย เป็นโครงการไม่ใหญ่มาก สงบ ร่มรื่นไม่วุ่นวาย ใกล้สถานศึกษา ใกล้ห้าง และโรงพยาบาลเหมาะกับน้อง เดินทางสะดวกด้วยค่ะ</span>", PostCategories = PostCategories.DLFamily, PostSubCategories = PostSubCategories.Testimonails, TitleImage = "images/post/family/2/DL-Family_02.jpg", Content = "<div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"><pstyle=\"margin:0px0px15px\">ที่ตัดสินใจซื้อที่นี่ตอนนั้นไม่รู้จักแบรนด์ดรีมแลนด์เลยไม่ได้สนใจเรื่องแบรนด์เลยดูสงบแล้วมีสนามเด็กเล่นเป็นโครงการ<br/>ไม่ใหญ่มากสงบร่มรื่นไม่วุ่นวายใกล้สถานศึกษาใกล้ห้างและโรงพยาบาลเหมาะกับน้องเดินทางสะดวกด้วยค่ะ</p></div></div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"></div></div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"><pstyle=\"margin:0px0px15px\"><bstyle=\"font-weight:bolder\">ครอบครัวคุณติ๊กอยู่ที่ดรีมแลนด์มานานหรือยังค่ะ</b><br/>นานแล้วค่ะประมาณ5ปี</p></div></div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"></div></div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"><pstyle=\"margin:0px0px15px\"><bstyle=\"font-weight:bolder\">เริ่มต้นดูยังไงคะถึงมาเจอที่นี่ได้ค่ะ</b><br/>บ้านคุณแม่อยู่แถวจรัญ13จากเดิมเราย้ายมาจากลาดพร้าว<br/>อยากมาอยู่ใกล้บ้านแม่เห็นป้ายเลยตามเข้ามาดูพอเข้ามาดู<br/>แล้วพอเห็นรู้สึกว่าใช่เลยอยากได้บ้านประมาณนี้<br/>มีเปรียบเทียบกับที่อื่นมีไปดูที่อื่นบ้างแต่ยังไม่ปิ๊งค่ะ</p></div></div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"></div></div><divclass=\"fw-row\"style=\"margin-left:-15px;margin-right:-15px;color:rgb(70,70,70);font-family:&quot;OpenSans&quot;;font-size:15px;background-color:rgb(255,255,255)\"><divclass=\"fw-col-xs-12\"style=\"position:relative;min-height:1px;padding-left:15px;padding-right:15px\"><pstyle=\"margin:0px0px15px\"><bstyle=\"font-weight:bolder\">คุณติ๊กประทับใจกับที่นี่ตรงไหนบ้างค่ะ</b><br/>ที่ตัดสินใจซื้อที่นี่ตอนนั้นไม่รู้จักแบรนด์ดรีมแลนด์เลย<br/>ไม่ได้สนใจเรื่องแบรนด์เลยเจอป้ายเลยลองเข้ามาดู<br/>พอเราเข้ามาดูเห็นบ้านจริงปุ๊ปแล้วเราซื้อเลยจ่ายแล้วโอนเลย<br/>ด้วยรูปลักษณ์ขนาดบ้านและสไตล์ที่ดูอบอุ่นดูสงบ<br/>แล้วมีสนามเด็กเล่นเป็นโครงการไม่ใหญ่มากสงบร่มรื่นไม่วุ่นวาย</p><pstyle=\"margin:0px0px15px\">ตอนนี้อยู่มา5ปีแล้วอีกเรื่องที่ประทับใจคือสะดวกใกล้สถานศึกษา<br/>ใกล้ห้างและโรงพยาบาลเหมาะกับน้อง(ลูกสาว)</p></div></div><br/></div>", CreatedAt = new DateTime(2017, 11, 12), ShowGallery = false });
                    postItemList.Add(new PostItem { Id = new Guid("0DA09C3D-7545-4DB2-A839-A64580F906FE"), PostId = 3, Name = "“เลือกที่นี่ก็เพราะวัสดุโครงการดี”", Description = "<span style=\"color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\">ที่นี่ใช้วัสดุดี หมู่บ้านเงียบสงบดี และ ชอบที่ห้องใหญ่พอดี ฟังก์ชั่นการใช้สอยดี แปลนบ้านดี หมู่บ้าน สะอาด ทางเซลล์และทีมงานก็บริการดีด้วยครับ</span>", PostCategories = PostCategories.DLFamily, PostSubCategories = PostSubCategories.Testimonails, TitleImage = "images/post/family/3/DL-Family_03.jpg", Content = "<div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"></div></div></div><section class=\"fw-main-row \" style=\" display: block; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-container\" style=\" position: relative; margin-right: auto; margin-left: auto; padding-left: 15px; padding-right: 15px; max-width: 100%; width: 1170px\"><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\">ผมเข้ามาดูตั้งแต่โครงการกำลังสร้าง คุณพ่อก็ชอบ เพราะพอจะซื้อก็ต้องไปดูของจริงดูวัสดุจริง ที่นี่ใช้วัสดุดี<br />หมู่บ้านเงียบสงบดี และ ชอบที่ห้องใหญ่พอดี ฟังก์ชั่นการใช้สอยดี แปลนบ้านดี หมู่บ้าน สะอาด<br />ทางเซลล์และทีมงานก็บริการดีด้วยครับ</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">ครอบครัว คุณ</b><b style=\" font-weight: bolder\">ธชชภ</b><b style=\" font-weight: bolder\">ณ&nbsp;</b><b style=\" font-weight: bolder\">ปัญจ</b><b style=\" font-weight: bolder\">พงศภัค (คุณอู๊ด)</b><br />จากโครงการ Dream Deluxe</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">คุณอู๊ดช่วยเล่าเรื่องตั้งแต่เริ่มดูบ้านจนมาลงเอยที่นี่หน่อยค่ะ</b><br />จริงๆก็ใช้เวลาดูไม่นานประมาณ 2 เดือน<br />โซนนี้ก็ดูหลายโครงการเหมือนกัน<br />ที่เลือกแถวนี้ก็เพราะใกล้บ้านคุณพ่อ บ้านคุณพ่ออยู่ตลิ่งชัน<br />แต่เดิมผมอยู่ในเมือง</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">แล้วทำไมคุณอู๊ดถึงเลือกที่นี่คะ</b><br />คือตอนเข้ามาดู ชอบที่ใช้วัสดุโครงการค่อนข้างดี แล้วก็ชอบห้องน้ำ<br />เข้ามาดูตั้งแต่โครงการกำลังสร้าง<br />ตอนแรกยังไม่มีโครงการนี้ยังเป็นแค่ดรีมวิลล์<br />แต่ได้ข่าวโครงการนี้ก็เลยเข้ามาดู<br />คุณพ่อก็ชอบสุดท้ายก็ตัดสินใจเป็นที่นี่<br />ย้ายเข้ามาอยู่ประมาณกรกฏาคม 2560</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">พอเข้ามาอยู่แล้วคุณอู๊ดประทับใจอะไรบ้างคะ</b><br />เรื่องแรกที่ประทับใจที่นี่ คือทางเซลล์และทีมงานบริการดีครับ<br />ตัวบ้าน ฟังก์ชั่นการใช้สอยมันดี แปลนบ้านดี ห้องน้ำกว้าง ห้องใหญ่<br />พอดี โดยรวมก็เป็นหมู่บ้านที่เงียบสงบดีนะ เรื่องความสะอาด<br />หมู่บ้านคอยมีคนทำคอยความสะอาดอยู่ตลอดเวลา</p></div></div></div></section><section class=\"fw-main-row \" style=\" display: block; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-container\" style=\" position: relative; margin-right: auto; margin-left: auto; padding-left: 15px; padding-right: 15px; max-width: 100%; width: 1170px\"><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><br class=\"Apple-interchange-newline\" /></div></div></div></section>", CreatedAt = new DateTime(2017, 11, 13), ShowGallery = false });
