                    postItemList.Add(new PostItem { Id = new Guid("E0843910-9D08-4F9C-ADD4-37063909E553"), PostId = 19, Name = "“เจอปัญหาอะไรนิดหน่อยเค้าก็รีบมาทำให้…เค้าใส่ใจตรงเนี้ย”", Description = "<span style=\"color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\">ช่างเค้าตามง่ายมาก โฟร์แมนก็ตามง่าย เจอปัญหาอะไรนิดหน่อย เค้าก็รีบมาทำให้ เค้าใส่ใจตรงเนี้ย เด่นที่สุดคือเซลล์เค้าใส่ใจ บริการดี พูดดี ต้อนรับเรา ยิ้มแย้ม เป็นความประทับใจมาก</span>", PostCategories = PostCategories.DLFamily, PostSubCategories = PostSubCategories.Testimonails, TitleImage = "images/post/family/19/DL-Family_19.jpg", Content = "<div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\">แล้วช่างเค้าตามง่ายมากเลยนะ โฟร์แมนก็ตามง่าย เจอปัญหาอะไรนิดหน่อย เค้าก็รีบมาทำให้ เค้าใส่ใจตรงเนี้ย<br />เด่นที่สุดคือเซลล์ เค้าใส่ใจ บริการดี พูดดี ต้อนรับเรา ยิ้มแย้ม เป็นความประทับใจมากค่ะ</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">ครอบครัว คุณเจนจิรา ทัพโยธา (คุณจุ๊บแจง)</b><br />จากโครงการ Dream Deluxe</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">ช่วยเล่าเรื่องตั้งแต่เริ่มดูบ้านจนมาซื้อที่นี่หน่อยครับ</b><br />เราก็ไปดูมาเยอะ หลายโครงการมากค่ะ<br />ต้องเล่าก่อนเลยว่าคุณแม่<br />ทำงานอยู่แถวๆนี้ด้วย<br />ดูตั้งแต่โครงการดรีมวิลล์<br />แต่เรายังไม่พร้อม<br />จนสุดท้ายเรามาได้ที่นี่ ดรีมดีลักซ์ค่ะ</p><p style=\" margin: 0px 0px 15px\">พอมาดูข้างในแล้วมันใช่เลยอ่ะ<br />ห้องน้ำ ห้องนอน ห้องอะไรมันโล่ง<br />อากาศดีด้วย น่าอยู่ โล่ง สะอาด<br />เป็นสัดเป็นส่วน ถนนก็กว้าง</p><p style=\" margin: 0px 0px 15px\">ทางเข้าเป็นสัดเป็นส่วน<br />เป็นระเบียบ สะอาด<br />ของที่อื่น มันจะเป็นซอย<br />ยื่นออกมาเยอะแยะอะไรอย่างนี้<br />มันดูไม่เป็นระเบียบ</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">แล้วไปดูของแบรนด์อื่นๆมาเยอะมากแล้ว</b><br /><b style=\" font-weight: bolder\">แล้วของเราไม่มีแบรนด์ไม่ได้คิดอะไร</b><b style=\" font-weight: bolder\">หรอ</b><b style=\" font-weight: bolder\">ครับ</b><br />ไม่นะคะ เราดูภาพรวม<br />ว่าตรงไหนที่บรรยากาศโครงการดี<br />ดูโล่ง แล้วอีกอย่างเซลล์ก็อัธยาศัยดี<br />เราดูมาตั้งแต่โครงการแรก<br />เค้าก็ยิ้มแย้มต้อนรับดี<br />พาเราดูตลอด อันนี้ก็เป็นด่านแรก<br />ที่ทำให้รู้สึกประทับใจ<br />ที่นี่ทุกอย่างลงตัว</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">แล้วอยู่มานานขนาดไหนแล้วครับ</b><br />สิ้นเดือนนี้ก็ปีนึงแล้วค่ะ</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">แล้วอยู่มาปีนึงเป็นยังไงบ้างครับ</b><br />ดีนะ พอเข้ามาอยู่แล้วมันรู้สึก<br />ได้พักผ่อนด้วยอากาศ<br />เพื่อนบ้านก็โอเค<br />แล้วมันรู้สึกปลอดภัยด้วย<br />เพราะเวลาอยู่บ้านเราก็เปิดผ้าม่าน<br />เหล็กดัดเราก็ไม่ได้ติดเลยนะ<br />เพราะเรามั่นใจว่า<br />ความปลอดภัยที่นี่ดีพอ<br />เราเลยไม่ต้องติดกรงเหล็ก</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">อยากให้ช่วยพูดความประทับใจเกี่ยวกับโครงการสั้นๆ</b><br />สบายใจค่ะ<br />เพราะเป็นที่ที่เราต้องใช้เวลา<br />อยู่กับมันเพื่อผ่อนคลาย<br />เพื่อรีแลกซ์จากการทำงาน<br />เป็นที่ที่อยู่แล้วเราสบายใจค่ะ<br />Feel Good 55555</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\"><b style=\" font-weight: bolder\">มีข้อแนะนำอะไรไหมครับ</b><br />ไม่มีค่ะ ก็ดีอยู่แล้ว<br />แล้วช่างเค้าตามง่ายมากเลยนะ<br />โฟร์แมนก็ตามง่าย<br />เจอปัญหาอะไรนิดหน่อย<br />เค้าก็รีบมาทำให้ เค้าใส่ใจตรงเนี้ย<br />ป้าก็แนะนำคนที่อมรินทร์มาตั้งหลายหลังแล้ว<br />เด่นที่สุดคือเซลล์<br />เค้าใส่ใจ บริการดี พูดดี<br />ต้อนรับเรา ยิ้มแย้ม<br />เป็นความประทับใจมากค่ะ</p></div></div>", CreatedAt = new DateTime(2017, 11, 29), ShowGallery = false });
