                    postItemList.Add(new PostItem { Id = new Guid("DE4762C7-A53D-43A3-A3C5-697561D64EE5"), PostId = 2, Name = "ดรีมดีลักซ์ โครงการที่พยาบาลศิริราชเลือก", PostCategories = PostCategories.DreamDeluxe, PostSubCategories = PostSubCategories.Hightlight, TitleImage = "https://www.dreamland.co.th/wp-content/uploads/2022/05/Blog-Content_Highlights_02-1920x1120.jpg", Content = "<div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\">แน่นอนว่าการเลือกซื้อบ้านถือเป็นการตัดสินใจที่ใหญ่และสำคัญของคนๆหนึ่ง การเลือกซื้อบ้านจึงต้องพิจารณาปัจจัยหลายๆ อย่าง แล้วทำไมต้องเลือกซื้อบ้านกับ Dreamland?</p><p style=\" margin: 0px 0px 15px\">ทำไมพยาบาลศิริราชถึงเลือกมาอยู่กับเรา?</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 30px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><h6 style=\" margin: 0px 0px 10px; line-height: normal; clear: both; font-size: 20px; font-family: &quot;Playfair Display&quot;; color: rgb(3, 3, 3)\"><b style=\" font-weight: bolder\"><font color=\"#0b54a3\">1. ใช้เวลาเพียง 20 นาทีในการเดินทาง</font></b></h6><p style=\" margin: 0px 0px 15px\">จากโครงการ Dream Deluxe ไปถึงศิริราช ใช้เวลาเพียง 20 นาที เพราะมีรถสองแถววิ่งผ่านหน้าโครงการตรงไปถึงศิริราชเลย ค่าโดยสารก็ราคาย่อมเยาว์มากเพียง 7.5 บาทเท่านั้นยิ่งไปกว่านั้นถนนราชพฤกษ์สามารถพาคุณไปได้ทุกเส้นทางในกรุงเทพ เนื่องจากถนนราชพฤกษ์เป็นถนนที่เป็นเส้นทางระยะยาว ขับรถสบายไม่มีไฟเขียวไฟแดง ทั้งยังเชื่อมต่อกับถนนเส้นหลักหลายเส้น เช่น ถนนพระราม 5, ถนนกาญจนาภิเษก, ถนนนครอินทร์, ถนนรัตนาธิเบศร์, ถนนบรมราชชนนี และถนนสาทร ถ้าต้องการขับเข้าใจกลางเมือง ก็ไม่ต้องกลัวพลาดนัดสำคัญ เพียงมีรถยนต์ส่วนตัวก็สามารถเดินทางเข้าใจกลางเมืองได้ภายในเวลาไม่เกินครึ่งชั่วโมง ซึ่งในอนาคตก็มีแผนขยายถนนจาก 6 เลนเป็น 10 เลนอีก หรือถ้าจะนั่งรถโดยสารประจำทางก็มีหลายสายที่ผ่านถนนราชพฤกษ์ ได้แก่ สาย 89, 710 และ 751 ส่วนรถไฟฟ้าตอนนี้ก็มีสายสีเขียวเข้ม สถานีบางหว้า ซึ่งในอนาคตก็จะมีโครงการรถไฟฟ้าสายสีแดงอ่อน (บางซื่อ-ตลิ่งชัน) ที่จะมาถึงบรมราชชนนี</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><h6 style=\" margin: 0px 0px 10px; line-height: normal; clear: both; font-size: 20px; font-family: &quot;Playfair Display&quot;; color: rgb(3, 3, 3)\"><b style=\" font-weight: bolder\"><span style=\" color: rgb(11, 84, 163)\">2. รายล้อมไปด้วย<span>&nbsp;</span></span></b><b style=\" font-weight: bolder\"><span style=\" color: rgb(11, 84, 163)\">Community Mall&nbsp;</span></b><b style=\" font-weight: bolder\"><span style=\" color: rgb(11, 84, 163)\">มากมาย</span></b></h6><p style=\" margin: 0px 0px 15px\">แม้โครงการ Dream Deluxe จะตั้งอยู่ในเขตพื้นที่สีเขียว แต่กลับรายล้อมไปด้วยความเจริญและสิ่งอำนวยความสะดวกอย่าง Community Mall มากมาย เช่น The Crystal SB ราชพฤกษ์, The Walk ราชพฤกษ์, Homepro, Homework, The Circle และ Central ปิ่นเกล้า ทำให้ผู้อยู่อาศัยได้มีความสะดวกสบายมากยิ่งขึ้น ไม่ต้องเจอปัญหารถติดเพื่อออกไปศูนย์การค้า อีกทั้งยังเป็นการออกมาเพื่อพักผ่อนสบายๆได้ในวันเสาร์ อาทิตย์อีกด้วย</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><h6 style=\" margin: 0px 0px 10px; line-height: normal; clear: both; font-size: 20px; font-family: &quot;Playfair Display&quot;; color: rgb(3, 3, 3)\"><strong style=\" font-weight: bold\"><span style=\" color: rgb(11, 84, 163)\">3. คุณภาพของบ้านที่แข็งแรงกว่ามาตรฐานบ้านทั่วไป</span></strong></h6><p style=\" margin: 0px 0px 15px\">บ้านของ Dreamland นั้น มีโครงสร้างแข็งแรงกว่ามาตรฐานบ้านทั่วไป และเก็บรายละเอียดของบ้านทุกหลังเหมือนบ้านของเราเอง นอกจากนี้ บริเวณถนนหน้าบ้านก็กว้างมากๆ ทำให้อากาศบริเวณรอบๆบ้านไม่แอดอัดเกินไป และทำให้มีพื้นที่ใช้สอยในการทำกิจกรรมต่างๆได้มากขึ้นในวันเสาร์ อาทิตย์ เช่น การขี่จักรยาน หรือการตีแบด</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 10px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><h6 style=\" margin: 0px 0px 10px; line-height: normal; clear: both; font-size: 20px; font-family: &quot;Playfair Display&quot;; color: rgb(3, 3, 3)\"><b style=\" font-weight: bolder\"><font color=\"#0b54a3\">4. มีระบบรักษาความปลอดภัยตลอด 24</font></b>&nbsp;<b style=\" font-weight: bolder\"><font color=\"#0b54a3\">ชม.</font></b></h6><p style=\" margin: 0px 0px 15px\">จึงมั่นใจได้ว่าบ้านของคุณจะปลอดภัยอย่างแน่นอน ไม่ว่าคุณจะไปพักผ่อนนานขนาดไหน ก็ทำให้คุณสบายใจ หายห่วงด้วยระบบรักษาความปลอดภัยของเรา</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 30px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\">ตอบรับชีวิตที่ลงตัวดุจบ้านเดี่ยว บนพื้นที่มากกว่า 20 ตารางวา<br />เราสร้างทาวน์โฮมคุณภาพด้วยโครงสร้างและวัสดุที่มีคุณภาพ<br />เพื่อครอบครัวของคุณ</p><p style=\" margin: 0px 0px 15px\">เพราะเราเชื่อว่า…ความสุขเริ่มต้นที่ “บ้าน”</p></div></div>", VideoLink = null, CreatedAt = new DateTime(2017, 11, 1), ShowGallery = false });
                    postItemList.Add(new PostItem { Id = new Guid("58B4A4F5-6EAD-4687-B913-71F1A58FCC56"), PostId = 1, Name = "DREAMLAND CLUBHOUSE", PostCategories = PostCategories.Dreamland, PostSubCategories = PostSubCategories.Hightlight, TitleImage = "https://www.dreamland.co.th/wp-content/uploads/2022/06/Blog-Content_Highlights_01_-1920x1120.jpg", Content = "<section class=\"fw-main-row \" style=\" display: block; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-container\" style=\" position: relative; margin-right: auto; margin-left: auto; padding-left: 15px; padding-right: 15px; max-width: 100%; width: 1170px\"><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"></div></div></div></section><section class=\"fw-main-row \" style=\" display: block; color: rgb(70, 70, 70); font-family: &quot;Open Sans&quot;; font-size: 15px; background-color: rgb(255, 255, 255)\"><div class=\"fw-container\" style=\" position: relative; margin-right: auto; margin-left: auto; padding-left: 15px; padding-right: 15px; max-width: 100%; width: 1170px\"><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><p style=\" margin: 0px 0px 15px\">Clubhouse ของ Dreamland เรามีบริการอาหาร ขนมต่างๆ ไว้สำหรับใครที่ไม่อยากออกไปไหนไกล หรือไม่อยากทำอาหารที่บ้านเอง ก็แวะมาฝากท้องกันได้ที่นี่เลย</p></div></div><div class=\"fw-row\" style=\" margin-left: -15px; margin-right: -15px\"><div class=\"fw-col-xs-12\" style=\" position: relative; min-height: 1px; padding-left: 15px; padding-right: 15px\"><div class=\"fw-divider-space\" style=\" clear: both; width: 1130px; padding-top: 20px\"></div></div></div><div class=\"fw-row\" style=\" margin-left: -15px; ma